#include <stdio.h>
#include <string.h>

char line[] = "";

void foo(const int N) {
    int found;
    const int len = strlen(line);
    for (int i = 0; i < len - N; ++i) {
        found = 0;
        for (int j = i; j < i + N; ++j) {
            for (int k = j + 1; k < i + N; ++k) {
                const char c1 = line[j];
                const char c2 = line[k];
                if (c1 == c2) {
                    found = 1;
                    break;
                }
            }
            if (found) {
                break;
            }
        }
        if (!found) {
            printf("i: %d\n", i + N);
            break;
        }
    }
}

int main() {
    foo(4);
    foo(14);
}
